import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { CampaignRepositoryPort } from '../../domain/ports/out/campaign-repository.port';
import { UploadResponseDto } from '../../domain/models/campaign.model';
import { environment } from '../../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class CampaignRepositoryAdapter implements CampaignRepositoryPort {
  private apiUrl = `${environment.apiUrl}/api/campaigns/upload`;

  constructor(private http: HttpClient) {}

  uploadCampaigns(file: File): Observable<UploadResponseDto> {
    const formData = new FormData();
    formData.append('file', file);

    return this.http.post<UploadResponseDto>(this.apiUrl, formData).pipe(
      catchError(this.handleError)
    );
  }

  private handleError(error: HttpErrorResponse) {
    let errorMessage = 'An unknown error occurred!';
    if (error.error instanceof ErrorEvent) {
      // Client-side errors
      errorMessage = `Error: ${error.error.message}`;
    } else {
      // Server-side errors
      if (typeof error.error === 'string') {
          errorMessage = error.error;
      } else {
          errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
      }
    }
    return throwError(errorMessage);
  }
}
